package calc;

public class Calc01 {
	
	
	public Calc01() {
		// TODO Auto-generated constructor stub
	}
	
	public int sum(int a, int b) {
		return a + b;
	}
	public int sub(int a, int b) {
		return a - b;
	}
	public int mul(int a, int b) {
		return a * b;
	}
	public double div(int a, int b) {
		return (double)a / b;
	}
}
